package com.javaex.practice;

public class Ex03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		코드만 보고 run 결괏값 예상헤보기
		int x, y;
		for (x=0; x<4; x++) {
			for (y=2;y>=0;y--) {
				System.out.println("*");
			}
			System.out.println("");
		}
	}

}
